import { useState } from "react";
import { motion } from "framer-motion";

function App() {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const scanUrl = async () => {
    if (!url.trim()) return;

    setLoading(true);
    setResult(null);

    try {
      const res = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ url }),
      });

      const data = await res.json();
      setResult(data);
    } catch (error) {
      console.error("Error:", error);
      setResult({ prediction: "error", confidence: 0 });
    }

    setLoading(false);
  };

  // Confidence FIX: Show safeness confidence for safe URLs
  const getConfidence = () => {
    if (!result) return 0;
    return result.prediction === "phishing"
      ? result.confidence * 100
      : (1 - result.confidence) * 100;
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-black via-slate-950 to-black text-slate-100 flex items-center justify-center px-4">
      <div className="w-full max-w-2xl">

        {/* Title */}
        <h1 className="text-4xl font-extrabold mb-4 text-center">
          <span className="text-emerald-400">AI</span> Phishing URL Detector
        </h1>

        <p className="text-slate-400 text-center mb-10">
          Paste a URL below and let the model analyze it in real time.
        </p>

        {/* Input + Button */}
        <div className="flex items-center gap-3 mb-6">
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            placeholder="Enter a URL..."
            className="flex-1 px-4 py-3 rounded-lg bg-slate-900 border border-slate-700 text-slate-200 focus:outline-none focus:border-emerald-400"
          />

          <button
            onClick={scanUrl}
            disabled={loading}
            className="px-6 py-3 bg-emerald-500 hover:bg-emerald-600 disabled:bg-slate-700 transition text-black font-bold rounded-lg"
          >
            {loading ? "Scanning..." : "Scan"}
          </button>
        </div>

        {/* Result */}
        {result && result.prediction !== "error" && (
          <motion.div
            initial={{ opacity: 0, y: 25 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
            className={`mt-8 p-6 rounded-xl border ${
              result.prediction === "phishing"
                ? "border-red-500"
                : "border-emerald-400"
            } bg-gradient-to-br from-[#050816] via-[#020617] to-black shadow-xl`}
          >
            {/* Header */}
            <h2
              className={`text-2xl font-bold mb-3 ${
                result.prediction === "phishing"
                  ? "text-red-400"
                  : "text-emerald-400"
              }`}
            >
              {result.prediction === "phishing" ? "⚠️ PHISHING URL" : "✅ SAFE URL"}
            </h2>

            {/* URL Display */}
            <p className="text-slate-300 mb-3 break-all">{result.url}</p>

            {/* Confidence */}
            <p className="text-slate-400 mb-2">
              Confidence:{" "}
              <span className="font-semibold">
                {getConfidence().toFixed(2)}%
              </span>
            </p>

            {/* Confidence Bar */}
            <div className="w-full bg-slate-800 h-2 rounded-full overflow-hidden">
              <div
                className={`h-full ${
                  result.prediction === "phishing"
                    ? "bg-red-500"
                    : "bg-emerald-400"
                }`}
                style={{ width: `${getConfidence()}%` }}
              ></div>
            </div>
          </motion.div>
        )}

        {/* Error Case */}
        {result && result.prediction === "error" && (
          <p className="text-red-400 text-center mt-6">
            Something went wrong. Try again.
          </p>
        )}
      </div>
    </div>
  );
}

export default App;
