import { useState, useEffect } from "react";
import { motion } from "framer-motion";

function App() {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [scanMessage, setScanMessage] = useState("Booting hologram scanner…");

  const scanMessages = [
    "Initializing AI hologram systems…",
    "Decrypting URL structure…",
    "Analyzing anomaly signatures…",
    "Scanning domain threat intelligence…",
    "Cross-referencing hologram neural maps…",
    "Finalizing result from cybercore…",
  ];

  const cycleMessages = () => {
    let index = 0;
    const interval = setInterval(() => {
      setScanMessage(scanMessages[index % scanMessages.length]);
      index++;
    }, 900);
    return interval;
  };

  const scanUrl = async () => {
    if (!url.trim()) return;

    setLoading(true);
    setResult(null);

    const interval = cycleMessages();

    try {
      const res = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ url }),
      });

      const data = await res.json();

      setTimeout(() => {
        clearInterval(interval);
        setResult(data);
        setLoading(false);
      }, 3500);

    } catch (error) {
      console.error(error);
      clearInterval(interval);
      setLoading(false);
    }
  };

  const getConfidence = () => {
    if (!result) return 0;
    return result.prediction === "phishing"
      ? result.confidence * 100
      : (1 - result.confidence) * 100;
  };

  const getRiskLevel = () => {
    const c = result.confidence;
    if (c < 0.20) return "Low Risk";
    if (c < 0.65) return "Medium Risk";
    return "High Risk";
  };

  /* BLUE MATRIX BACKGROUND */
  useEffect(() => {
    const canvas = document.getElementById("matrixCanvasD");
    const ctx = canvas.getContext("2d");

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const letters = "01";
    const columns = canvas.width / 20;
    const drops = [];

    for (let i = 0; i < columns; i++) drops[i] = 1;

    function draw() {
      ctx.fillStyle = "rgba(0, 0, 0, 0.06)";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = "#00d9ff";
      ctx.font = "15px monospace";

      for (let i = 0; i < drops.length; i++) {
        const text = letters[Math.floor(Math.random() * letters.length)];
        ctx.fillText(text, i * 20, drops[i] * 20);

        if (drops[i] * 20 > canvas.height || Math.random() > 0.95)
          drops[i] = 0;

        drops[i]++;
      }
    }

    const interval = setInterval(draw, 50);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-black relative overflow-hidden">

      {/* MATRIX HOLOGRAM BACKGROUND */}
      <canvas
        id="matrixCanvasD"
        className="absolute inset-0 opacity-30 pointer-events-none"
      ></canvas>

      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen px-6">

        {/* TITLE */}
        <motion.h1
          className="text-5xl font-extrabold text-center mb-4 tracking-wide text-blue-400 hologram-title"
          initial={{ opacity: 0, y: -15 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          Hologram Phishing Detector
        </motion.h1>

        <motion.p
          className="text-blue-200 mb-12 text-center text-lg"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4 }}
        >
          Futuristic threat intelligence powered by hologram AI.
        </motion.p>

        {/* INPUT BAR */}
        <motion.div
          className="flex w-full max-w-3xl gap-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.2 }}
        >
          <input
            className="flex-1 px-5 py-4 rounded-xl bg-blue-900/40 border border-blue-500/40 
                       text-blue-100 focus:border-blue-400 focus:ring-4 focus:ring-blue-500/25 
                       transition-all duration-300 backdrop-blur-xl neon-blue-input"
            placeholder="Enter a suspicious URL…"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />

          <motion.button
            onClick={scanUrl}
            disabled={loading}
            whileHover={{ scale: 1.05, boxShadow: "0 0 20px #00d9ff" }}
            whileTap={{ scale: 0.95 }}
            className="px-8 py-4 rounded-xl font-bold bg-blue-500 hover:bg-blue-600 
                       active:scale-[0.98] text-black shadow-blue-glow transition-all duration-300"
          >
            {loading ? "Scanning…" : "Scan"}
          </motion.button>
        </motion.div>

        {/* LOADING */}
        {loading && (
          <div className="flex flex-col items-center mt-16">
            <motion.div
              className="radar-blue mb-6"
              initial={{ scale: 0.6, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
            >
              <div className="radar-blue-core"></div>
            </motion.div>

            <motion.p
              key={scanMessage}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
              className="text-blue-300 text-xl tracking-wide"
            >
              {scanMessage}
            </motion.p>
          </div>
        )}

        {/* RESULT */}
        {result && !loading && (
          <motion.div
            className="mt-12 p-8 rounded-2xl border border-blue-400/40 bg-white/10 backdrop-blur-xl 
                       shadow-2xl hologram-blue w-full max-w-3xl relative overflow-hidden"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7 }}
          >
            <span className="border-trace-blue"></span>

            <h2
              className={`text-3xl font-bold mb-4 ${
                result.prediction === "phishing"
                  ? "text-red-400"
                  : "text-blue-300"
              }`}
            >
              {result.prediction === "phishing"
                ? "⚠️ PHISHING DETECTED"
                : "✓ SAFE URL"}
            </h2>

            <p className="text-blue-200 mb-4 break-all text-lg">{result.url}</p>

            <p className="text-blue-300">
              Risk Level: <span className="font-semibold">{getRiskLevel()}</span>
            </p>

            <p className="text-blue-300 mt-2 mb-2">
              Confidence: {getConfidence().toFixed(2)}%
            </p>

            {/* Hologram Animated Confidence Bar */}
            <div className="w-full h-3 bg-blue-900/60 rounded-full overflow-hidden mt-2">
              <motion.div
                initial={{ width: 0 }}
                animate={{ width: `${getConfidence()}%` }}
                transition={{ duration: 1.3, ease: "easeOut" }}
                className="h-full bg-blue-400 rounded-full shadow-lg"
              />
            </div>
          </motion.div>
        )}

        <p className="text-blue-200 text-sm mt-10 mb-4 text-center font-medium opacity-90">
          ⚠ AI predictions may not be 100% accurate. Always double-check suspicious URLs.
        </p>

      </div>
    </div>
  );
}

export default App;
