import { useState, useEffect } from "react";
import { motion } from "framer-motion";

function App() {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [scanMessage, setScanMessage] = useState("Initializing scan…");

  const scanMessages = [
    "Scanning URL fingerprint…",
    "Checking structure & entropy…",
    "Analyzing obfuscation patterns…",
    "Validating domain signature…",
    "Cross-referencing threat models…",
    "Finalizing AI decision…",
  ];

  const cycleMessages = () => {
    let index = 0;
    const interval = setInterval(() => {
      setScanMessage(scanMessages[index % scanMessages.length]);
      index++;
    }, 900);
    return interval;
  };

  const scanUrl = async () => {
    if (!url.trim()) return;

    setLoading(true);
    setResult(null);

    const interval = cycleMessages();

    try {
      const res = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ url }),
      });

      const data = await res.json();

      setTimeout(() => {
        clearInterval(interval);
        setResult(data);
        setLoading(false);
      }, 3500);

    } catch (error) {
      console.error(error);
      clearInterval(interval);
      setLoading(false);
    }
  };

  const getConfidence = () => {
    if (!result) return 0;
    return result.prediction === "phishing"
      ? result.confidence * 100
      : (1 - result.confidence) * 100;
  };

  const getRiskLevel = () => {
    const c = result.confidence;
    if (c < 0.20) return "Low Risk";
    if (c < 0.65) return "Medium Risk";
    return "High Risk";
  };

  useEffect(() => {
    const canvas = document.getElementById("matrixCanvas");
    const ctx = canvas.getContext("2d");

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const letters = "01";
    const columns = canvas.width / 20;
    const drops = [];

    for (let i = 0; i < columns; i++) drops[i] = 1;

    function draw() {
      ctx.fillStyle = "rgba(0, 0, 0, 0.05)";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = "#00ff9d";
      ctx.font = "15px monospace";

      for (let i = 0; i < drops.length; i++) {
        const text = letters[Math.floor(Math.random() * letters.length)];
        ctx.fillText(text, i * 20, drops[i] * 20);

        if (drops[i] * 20 > canvas.height || Math.random() > 0.95)
          drops[i] = 0;

        drops[i]++;
      }
    }

    const interval = setInterval(draw, 60);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-black relative overflow-hidden">

      {/* MATRIX BACKGROUND */}
      <canvas
        id="matrixCanvas"
        className="absolute inset-0 opacity-20 pointer-events-none"
      ></canvas>

      <div className="relative z-10 flex flex-col items-center justify-center min-h-screen px-6">

        {/* TITLE */}
        <motion.h1
          className="text-5xl font-extrabold text-center mb-6 tracking-wide"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <span className="text-emerald-400">AI</span>{" "}
          <span className="text-white">Phishing URL Detector</span>
        </motion.h1>

        <p className="text-slate-400 mb-10 text-center text-lg">
          Cyber-grade threat intelligence powered by AI.
        </p>

        {/* INPUT SECTION */}
        <motion.div
          className="flex w-full max-w-3xl gap-3"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
        >
          <input
            className="flex-1 px-5 py-4 rounded-xl bg-slate-900/60 border border-slate-700 
                       text-slate-200 focus:border-emerald-400 focus:ring-4 
                       focus:ring-emerald-500/30 transition-all duration-300 backdrop-blur-xl"
            placeholder="Enter URL…"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />

          <button
            onClick={scanUrl}
            disabled={loading}
            className="px-8 py-4 bg-emerald-500 hover:bg-emerald-600 active:scale-[0.98]
                       rounded-xl text-black font-bold transition shadow-lg"
          >
            {loading ? "Scanning…" : "Scan"}
          </button>
        </motion.div>

        {/* SCANNING ANIMATION */}
        {loading && (
          <div className="flex flex-col items-center mt-14">
            <motion.div
              className="radar-container mb-6"
              initial={{ scale: 0.5, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
            >
              <div className="radar"></div>
            </motion.div>

            <motion.p
              key={scanMessage}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5 }}
              className="text-emerald-400 text-xl tracking-wide"
            >
              {scanMessage}
            </motion.p>
          </div>
        )}

        {/* RESULT CARD */}
        {result && !loading && (
          <motion.div
            className="mt-10 p-8 rounded-2xl border border-emerald-500/40 bg-white/10 
                       backdrop-blur-xl shadow-2xl hologram-card w-full max-w-3xl"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7 }}
          >
            <h2
              className={`text-3xl font-bold mb-4 ${
                result.prediction === "phishing" ? "text-red-400" : "text-emerald-400"
              }`}
            >
              {result.prediction === "phishing" ? "⚠️ PHISHING DETECTED" : "✅ SAFE URL"}
            </h2>

            <p className="text-slate-300 mb-4 break-all text-lg">{result.url}</p>

            <p className="text-slate-400">
              Risk Level: <span className="font-semibold">{getRiskLevel()}</span>
            </p>

            <p className="text-slate-400 mt-2 mb-2">
              Confidence: {getConfidence().toFixed(2)}%
            </p>

            {/* Animated Confidence Bar */}
            <div className="w-full h-3 bg-slate-800/60 rounded-full overflow-hidden mt-2">
              <motion.div
                initial={{ width: 0 }}
                animate={{ width: `${getConfidence()}%` }}
                transition={{ duration: 1.3, ease: "easeOut" }}
                className={`h-full ${
                  result.prediction === "phishing" ? "bg-red-500" : "bg-emerald-400"
                } rounded-full shadow-lg`}
              />
            </div>
          </motion.div>
        )}

        {/* ⭐ DISCLAIMER BELOW APP (NOT inside result card) */}
        <p className="text-slate-600 text-sm mt-10 italic text-center">
          ⚠ This AI tool is for assistance only. Predictions may not be 100% accurate.  
          Always verify suspicious URLs manually.
        </p>

      </div>
    </div>
  );
}

export default App;
