import { useState } from "react";
import { motion } from "framer-motion";

function App() {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const API_URL = "http://localhost:8000/predict";

  const checkUrl = async () => {
    setLoading(true);
    setResult(null);

    const res = await fetch(API_URL, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ url }),
    });

    const data = await res.json();
    setResult(data.prediction);
    setLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-[#080d19] px-4">
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7 }}
        className="w-full max-w-lg p-8 rounded-xl bg-[#0d1326] shadow-[0_0_25px_#00ff88]"
      >
        <h1 className="text-3xl font-bold text-center text-[#00ff88] mb-6 tracking-wide">
          üîê Phishing URL Detector
        </h1>

        <motion.input
          whileFocus={{ scale: 1.02, boxShadow: "0 0 12px #00ff88" }}
          transition={{ duration: 0.2 }}
          type="text"
          placeholder="Enter URL to scan"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          className="w-full px-4 py-3 rounded-md bg-[#0a0f1f] border border-[#00ff88] text-white mb-4 outline-none"
        />

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.9 }}
          onClick={checkUrl}
          className="w-full py-3 rounded-md bg-[#00ff88] font-bold text-[#0a0f1f]"
        >
          {loading ? "Scanning..." : "Scan URL"}
        </motion.button>

        {result && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
            className="mt-6 p-5 rounded-lg text-center border"
            style={{
              borderColor: result === "phishing" ? "#ff4444" : "#00ff88",
              boxShadow:
                result === "phishing"
                  ? "0 0 15px #ff4444"
                  : "0 0 15px #00ff88",
            }}
          >
            <h2
              className="text-2xl font-bold"
              style={{
                color: result === "phishing" ? "#ff4444" : "#00ff88",
              }}
            >
              {result.toUpperCase()}
            </h2>
          </motion.div>
        )}
      </motion.div>
    </div>
  );
}

export default App;
