import { useState } from "react";
import { motion } from "framer-motion";

function App() {
  const [url, setUrl] = useState("");
  const [result, setResult] = useState(null);

  const checkUrl = async () => {
    const res = await fetch("http://localhost:8000/predict", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ url }),
    });

    const data = await res.json();
    setResult(data);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-black via-slate-950 to-black text-slate-100 flex items-center justify-center px-4">
      <div className="w-full max-w-2xl">
        
        {/* Title */}
        <h1 className="text-3xl md:text-4xl font-extrabold mb-6 text-center">
          <span className="text-emerald-400">AI</span> Phishing URL Detector
        </h1>

        {/* Subtext */}
        <p className="text-slate-400 text-center mb-8">
          Paste a URL below and let the model analyze it in real time.
        </p>

        {/* Input field */}
        <div className="flex gap-3 mb-6">
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            className="flex-1 px-4 py-3 rounded-lg bg-slate-900 border border-slate-700 text-slate-200 focus:outline-none focus:border-emerald-400"
            placeholder="Enter a URL..."
          />
          <button
            onClick={checkUrl}
            className="px-6 py-3 bg-emerald-500 hover:bg-emerald-600 transition text-black font-bold rounded-lg"
          >
            Scan
          </button>
        </div>

        {/* Result card goes HERE */}
        {result && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4 }}
            className={`mt-8 p-6 rounded-xl border ${
              result.prediction === "phishing"
                ? "border-red-500"
                : "border-emerald-400"
            } bg-gradient-to-br from-[#050816] via-[#020617] to-black shadow-lg`}
          >
            <h2
              className={`text-2xl font-bold mb-2 ${
                result.prediction === "phishing"
                  ? "text-red-400"
                  : "text-emerald-400"
              }`}
            >
              {result.prediction === "phishing" ? "⚠️ PHISHING URL" : "✅ SAFE URL"}
            </h2>

            <p className="text-slate-300 mb-2 break-all">{result.url}</p>

            <p className="text-slate-400 mb-1">
              Confidence: {(result.confidence * 100).toFixed(2)}%
            </p>

            <div className="w-full bg-slate-800 h-2 rounded-full overflow-hidden">
              <div
                className={`h-full ${
                  result.prediction === "phishing" ? "bg-red-500" : "bg-emerald-400"
                }`}
                style={{ width: `${result.confidence * 100}%` }}
              ></div>
            </div>
          </motion.div>
        )}

      </div>
    </div>
  );
}

export default App;
